import com.mongodb.MongoClient;
import com.mongodb.MongoClientURI;
import com.mongodb.DB;



public class mongoconnect
{
	def log
	def testRunner
	def context
	def mongoconnect(log, testRunner, context)
	{
	this.log = log
	this.testRunner = testRunner
	this.context = context
	}

	def mongoconnecturl (String dbname)
	{
		String url = ${mongourl};
		MongoClientURI client = new MongoClientURI(url);
		MongoClient mongoClient = new MongoClient(client);
		DB db = mongoClient.getDB(dbname);
	}


}


context.setProperty("obj", new mongoconnect(log, testRunner, context))

