import java.io.*
import org.apache.poi.xssf.usermodel.*
import org.apache.poi.hssf.usermodel.*
import org.apache.poi.ss.usermodel.*
import java.io.BufferedWriter
import java.io.FileWriter


def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
String dataSheet = context.expand( '${DataSheet}' )
dataSheet = dataSheet.replace('/','\\\\')

def fs = new FileInputStream (dataSheet)
def wb = new XSSFWorkbook(fs)
def sh = wb.getSheet("TestController")
def rws = sh.getPhysicalNumberOfRows()


for (def i=1;i<rws;i++)
{
	def row = sh.getRow(i)
	def c = row.getPhysicalNumberOfCells()
	def TestCaseRunMode = sh.getRow(i).getCell(3)
	String datas = TestCaseRunMode


	if(datas.equalsIgnoreCase("N"))
	{

	 String TestSuitename = sh.getRow(i).getCell(1)
	 String TestCaseName = sh.getRow(i).getCell(2)
	 testRunner.testCase.testSuite.project.testSuites[TestSuitename].getTestCaseByName(TestCaseName).setDisabled(true)

	 log.info  " Test Case Name $TestCaseName"
	 }	
	 else if (datas.equalsIgnoreCase("Y"))
	 {
	 	 String TestSuitename = sh.getRow(i).getCell(1)
	      String TestCaseName = sh.getRow(i).getCell(2)
	     log.info  " Test Case Name For Valid $TestCaseName"
	 testRunner.testCase.testSuite.project.testSuites[TestSuitename].getTestCaseByName(TestCaseName).setDisabled(false)



	 	//def runner = testcaserun.run(null,false)
	 }

}

