con:config.script:
import org.openqa.selenium.By
import org.openqa.selenium.WebDriver
import org.openqa.selenium.WebElement
import org.openqa.selenium.Keys
import org.openqa.selenium.chrome.ChromeDriver
import java.util.concurrent.TimeUnit

flag = context.expand('${Properties#flag}')
//Initiate WebDriver
String chromedriverpath = context.expand( '${Chromedriverpath}' )
String chromepath = chromedriverpath.replace('\\','\\\\')

System.setProperty("webdriver.chrome.driver",chromepath);
String gmailurl =  context.expand( '${mail_url}' )
String gmail_Login = context.expand( '${Gmail_Login}' )
String gmail_password = context.expand( '${Gmail_password}' )
WebDriver driver=new ChromeDriver();
driver.get(gmailurl);
WebElement userID = driver.findElement(By.xpath("//input[@id='identifierId' and @type='email']"));
userID.sendKeys(gmail_Login);

WebElement userIDNextButton = driver.findElement(By.xpath("//*[@id='identifierNext']"));
userIDNextButton.click();
Thread.sleep(2000);
WebElement password = driver.findElement(By.xpath("//input[@name='password' and @type='password']"));
password.sendKeys(gmail_password);
WebElement passwordNextButton = driver.findElement(By.xpath("//*[@id='passwordNext']"));
passwordNextButton.click();
Thread.sleep(20000)
WebElement searchMail = driver.findElement(By.xpath("//*[@aria-label='Search mail' and @name='q']"));
searchMail.click();
email_address = context.expand('${Properties#email'+flag.toString()+'}')
log.info email_address
name = context.expand('${Properties#name'+flag.toString()+'}')
log.info name+flag.toString()
searchMail.sendKeys(email_address);
Thread.sleep(5000)
searchMail.sendKeys(Keys.RETURN);
Thread.sleep(5000)
WebElement firstMail = driver.findElement(By.xpath("//div[@class='yi']/following-sibling::span[contains(text(),'"+name+"')][1]"));
firstMail.click();

Thread.sleep(5000)

WebElement mailTextPath = driver.findElement(By.xpath("//div[@class='ii gt'][.//p[text()='Dear "+name+",']  and .//p[contains(text(),'Dear')]]"));
mailText = mailTextPath.getText();

url1 = CaptureConcentUrl(mailText);

WebElement mailLinkPath = driver.findElement(By.xpath("//tr/td/p/a[contains(text(),'"+url1+"')]"));
def hrefValue = mailLinkPath.getAttribute("href");
log.info hrefValue

def hashCode = CaptureHash(hrefValue)
testRunner.testCase.testSuite.project.testSuites["Groovy Scripts"].testCases["Owner Consent Email"].testSteps["Properties"].setPropertyValue("hashCode"+flag.toString(), hashCode)

Thread.sleep(5000)
assert hashCode != "" : "pass"
driver.quit();

public def String CaptureConcentUrl(String mailBodyContent) {
	String url=null;
	String[] msg=mailBodyContent.split("which will help us underwrite the application.");
	String[] urllink = msg[1].split("Once you have completed this process");
	url = urllink[0].trim();
	return url;
}

public def String CaptureHash(String urlToTrim) {
	String[]trimURL = null
	trimURL=urlToTrim.split("/owner-consent/");
	return trimURL[1].replace("/","").trim();
}

