//Initiate WebDriver
String chromedriverpath = context.expand( '${Chromedriverpath}' )
String chromepath = chromedriverpath.replace('\\','\\\\')
String gmailurl =  context.expand( '${mail_url}' )
String gmail_Login = context.expand( '${Gmail_Login}' )
String gmail_password = context.expand( '${Gmail_password}' )
System.setProperty("webdriver.chrome.driver",chromepath);

WebDriver driver=new ChromeDriver();
driver.get(gmailurl);
WebElement userID = driver.findElement(By.xpath("//input[@id='identifierId' and @type='email']"));
userID.sendKeys(gmail_Login);

WebElement userIDNextButton = driver.findElement(By.xpath("//*[@id='identifierNext']"));
userIDNextButton.click();
Thread.sleep(2000);
WebElement password = driver.findElement(By.xpath("//input[@name='password' and @type='password']"));
password.sendKeys(gmail_password);
WebElement passwordNextButton = driver.findElement(By.xpath("//*[@id='passwordNext']"));
passwordNextButton.click();
Thread.sleep(10000)
WebElement searchMail = driver.findElement(By.xpath("//*[@aria-label='Search mail' and @name='q']"));
searchMail.click();
String  email_address = context.expand( '${#[ICICI Webservice Suite_SignEmail#Valid: Sign In Verify Email#Auth credentials]#Email}' )
searchMail.sendKeys(email_address);
String name=  context.expand( '${#[ICICI Webservice Suite_SignEmail#Valid: Sign In Verify Email#Sign Up Email]#RawRequest#$[\'to_name\']}' )

Thread.sleep(2000)
searchMail.sendKeys(Keys.RETURN);
Thread.sleep(4000)
String namesearch = null
	if(name.contains("4977 N.W.T. LTD."))
	{
		namesearch = name.replace('.','')
	}
	else if(name.contains("MINETEC NORTH, A MINETEC SALESBOWS AND ARROWS JOINT VENTURE") )
	{
		namesearch = name.replace('JOINT VENTURE','JOINT')
	}
	else if(name.contains("Chp efgdfgh fhgdddddddddddd dfghhhhhhhhhhhhhhh fdgh'asddsd'asdasddsdsa'sad.asd.dsa.sda.sda.") )
	{
		namesearch = name.replace('hhhhhhhhh fdgh\'asddsd\'asdasddsdsa\'sad.asd.dsa.sda.sda.','h')
	}

	else
	{
	namesearch = name
	}

WebElement firstMail = driver.findElement(By.xpath("//div[@class='yi']/following-sibling::span[contains(text(),\""+namesearch+"\")][1]"));
firstMail.click();

Thread.sleep(5000)
if(name.contains("Chp efgdfgh fhgdddddddddddd dfghhhhhhhhhhhhhhh fdgh'asddsd'asdasddsdsa'sad.asd.dsa.sda.sda.") )
	{

		WebElement mailTextPath = driver.findElement(By.xpath("//div[@class='ii gt']//p[contains(text(),\"Dear "+name+",\")]"));
	mailText = mailTextPath.getText();
	}
	else
	{
		WebElement mailTextPath = driver.findElement(By.xpath("//div[@class='ii gt'][.//p[text()=\"Dear "+name+",\"]  and .//p[contains(text(),'Dear')]]"));
	mailText = mailTextPath.getText();
	}







url1 = CaptureConcentUrl(mailText);

WebElement mailLinkPath = driver.findElement(By.xpath("//tr/td/p/a[contains(text(),'"+url1+"')]"));
def hrefValue = mailLinkPath.getAttribute("href");

String url =  context.expand( '${#[ICICI Webservice Suite_SignEmail#Valid: Sign In Verify Email#DataSource]#URL}' )
String expecteddear = "dear $name,"
String header = "activation email"
assert mailText.toLowerCase().contains(header.toLowerCase()): "Failed due to Header Subject not matched"
assert  mailText.toLowerCase().contains(expecteddear.toLowerCase()) : "Failed due to To_name not matched"
assert  hrefValue.toLowerCase().contains(url.toLowerCase()) : "Failed due to URL is not matched"


driver.close();

public def String CaptureConcentUrl(String mailBodyContent) {
	String url=null;
	String[] msg=mailBodyContent.split("This is a one-time process.");
	String[] urllink = msg[1].split("If you have any questions,");
	url = urllink[0].trim();
	return url;
}

