public def String CaptureConcentUrl(String mailBodyContent) {
	String url=null;
	String[] msg=mailBodyContent.split("Simply click on the link below to reset your password.");
	String[] urllink = msg[1].split("If you did not send a request to change your password,");
	url = urllink[0].trim();
	return url;
}

