String url = context.expand( '${mongourl}' );
MongoClientURI client = new MongoClientURI(url);
MongoClient mongoClient = new MongoClient(client);
DB db = mongoClient.getDB("service_case"); 	

DBCollection collec = db.getCollection("owner_details");
DBCollection collechash = db.getCollection("owner_hash");
BasicDBObject query = new BasicDBObject();
BasicDBObject queryhash = new BasicDBObject();


String userId = context.expand( '${#[ICICI Webservice Suite_Owner#Valid: Owner Consent Email Send#Auth Create Application]#UserId}' ).toString()
testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("userIdToVerify",userId.toString())
String appId = context.expand( '${#[ICICI Webservice Suite_Owner#Valid: Owner Consent Email Send#Auth Create Application]#AppId}' ).toString()
testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("appIdToVerify",appId.toString())
String ownerType = 'individual'
String ownerConsentUrl = context.expand( '${#[ICICI Webservice Suite_Owner#Valid: Owner Consent Email Send#DataSource]#owner_consent_url}' ).toString();
String borrower_name = context.expand( '${#[ICICI Webservice Suite_Owner#Valid: Owner Consent Email Send#DataSource]#borrower_name}' ).toString();
String primary_owner_name = context.expand( '${#[ICICI Webservice Suite_Owner#Valid: Owner Consent Email Send#DataSource]#primary_owner_name}' ).toString();
String mail_type = context.expand( '${#[ICICI Webservice Suite_Owner#Valid: Owner Consent Email Send#DataSource]#mail_type}' ).toString();

query.append("app_id",appId.trim());
query.append("user_id",userId.trim());
query.append("owner_type",ownerType.trim());

int count = collec.find(query).count()

testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("count",count.toString())
def requestStartingBody = '''{
"consents": ['''
String requestBody
def requestEndingBody = "],\n"+"\"mail_type\"" +":"+ "\"$mail_type\""+"\n}"

def cursor = collec.find(query)

StringBuffer builder = new StringBuffer()
String response = null
int flag = 0
while (cursor.hasNext())
{
	DBObject doc = cursor.next();
	String  email_address = doc.get("owner_email").toString().trim();
	String ownerid = doc.get("_id").toString().trim();
	String  direct_owner_name = doc.get("name_of_the_direct_owner").toString().trim();
	queryhash.append("owner_id",ownerid)
	def cursorhash = collechash.find(queryhash)
	String hashcode = null
	while(cursorhash.hasNext())
	{
		hascode = cursorhash.next().get("hash").toString()

	}
	String newownerConsentUrl = ownerConsentUrl + hascode
	builder.append(
		"{\n"+"\"to\":\"$email_address\",\n"+"\"to_name\":\"$direct_owner_name\",\n"+"\"owner_consent_url\":\"$newownerConsentUrl\",\n"+"\"borrower_name\":\"$borrower_name\",\n"+"\"primary_owner_name\":\"$primary_owner_name\"\n"+"},"
	)

	testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("email"+flag.toString(),email_address.toString().trim())
	testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("name"+flag.toString(),direct_owner_name.toString().trim())

	flag++
}
def requestbody = builder
requestbody = requestbody.substring(0,requestbody.length()-1);
def request = requestStartingBody + requestbody + requestEndingBody
testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Post Owner Consent"].setPropertyValue("Request",request)


def postOwnerConsentStep = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Post Owner Consent"]
def testCase = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].getTestCaseByName("Valid: Owner Consent Email Send")
def groovyGmailHashCodeStep = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Groovy Script Gmail Get Hash Code"]
def getOwnerConsentStep = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Get Owner Consent"]
def assertionStep = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Assertion"]

if( postOwnerConsentStep.disabled.toString().trim() == "true" ){
	postOwnerConsentStep.disabled = false
}

def status = postOwnerConsentStep.run(testRunner, context);

if(status.getStatus().toString().trim() == "PASS")
{
	for(def i=0;i<count;i++)
	{
		testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("flag",i.toString())
		emailToVerify = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].getPropertyValue("email"+i.toString())
		testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("emailToVerify",emailToVerify)
		nameToVerify = testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].getPropertyValue("name"+i.toString())
		testRunner.testCase.testSuite.project.testSuites["ICICI Webservice Suite_Owner"].testCases["Valid: Owner Consent Email Send"].testSteps["Properties"].setPropertyValue("nameToVerify",nameToVerify)
		testRunnerGroovy = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testCase, null);
		testStepContextGroovy = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(groovyGmailHashCodeStep);
		groovyGmailHashCodeStep.run(testRunnerGroovy, testStepContextGroovy);

		testRunnerGetOwner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testCase, null);
		testStepContextGetOwner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(getOwnerConsentStep);
		hashValue = context.expand('${Properties#hashCode'+i.toString()+'}')
		log.info hashValue
		if( getOwnerConsentStep.disabled.toString().trim() == "true" ){
			getOwnerConsentStep.disabled = false
		}
		getOwnerConsentStep.setPropertyValue("hash",hashValue)
		Thread.sleep(2000);
		def getOwnerConsentStepStatus = getOwnerConsentStep.run(testRunner, context)
		Thread.sleep(40000);
		if(getOwnerConsentStepStatus.getStatus().toString().trim() == "FAIL")
		{
			testRunner.fail("Get Owner Consent Step Failed")
		}
		else{
			log.info "pass"
		}

		testRunnerAssertion = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testCase, null);
		testStepContextAssertion = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(assertionStep);

		if( assertionStep.disabled.toString().trim() == "true" ){
			assertionStep.disabled = false
		}
		assertionStep.run(testRunnerAssertion, testStepContextAssertion)
	}
}

mongoClient.close()

