String url = context.expand( '${mongourl}' );
MongoClientURI client = new MongoClientURI(url);
MongoClient mongoClient = new MongoClient(client);
DB db = mongoClient.getDB("service_case"); 	

DBCollection collec = db.getCollection("owner_details");


BasicDBObject query2 = new BasicDBObject();
BasicDBObject query3 = new BasicDBObject();
BasicDBObject query = new BasicDBObject();

String userID = context.expand( '${#[ICICI Webservice Suite_Canada Percentage#Valid: Canada Percentage#DataSource]#userID}' )
String appID = context.expand( '${#[ICICI Webservice Suite_Canada Percentage#Valid: Canada Percentage#DataSource]#AppID}' )

query.append("app_id",appID.trim());
query.append("user_id",userID.trim());
query.append("owner_type","individual");
query.append("canada_citizen", "yes")

def cursor = collec.find(query)
int count = collec.find(query).count()
List<Integer> percentagetotal = new ArrayList<Integer>()
for(def i = 0 ; i<count;i++)
{
DBObject doc = cursor.next();
	if(doc.get("parent_id").toString() != "0")
	{ 
		String parent_ownership_percent,ownership_percent = null
		String parentid = doc.get("parent_id").toString()
		 ownership_percent = doc.get("ownership_percent").toString()
		query2.append("_id",new ObjectId(parentid))

		def cursor2 = collec.find(query2)
		DBObject doc2 = cursor2.next();
		parent_ownership_percent = doc2.get("ownership_percent").toString()

		Integer parent_ownership_percents = Integer.valueOf(parent_ownership_percent);
		Integer ownership_percents =  Integer.valueOf(ownership_percent);
		Integer calculatepercent = (Integer) (ownership_percents*parent_ownership_percents)/100

			if (doc2.get("parent_id").toString() != "0")
			{
				String parentid2 = doc2.get("parent_id").toString()
				query3.append("_id",new ObjectId(parentid2))
				def cursor3 = collec.find(query3)
				doc2= cursor3.next();
				Integer parent_ownership_percent3 = Integer.valueOf(doc2.get("ownership_percent").toString())
				Integer calculatepercent3 = (Integer) (calculatepercent* parent_ownership_percent3)/100
				percentagetotal.add(calculatepercent3)
			}
			else 
			{
				percentagetotal.add(calculatepercent)
			}

			}
	else 
	{
	Integer calculatepercent = Integer.valueOf(ownership_percent);
	percentagetotal.add(calculatepercent)

	}

}
int total = 0
for (def j=0; j<percentagetotal.size();j++)
{
	 total = percentagetotal.get(j) +total

}
int  response_percentage = context.expand( '${#[ICICI Webservice Suite_Canada Percentage#Valid: Canada Percentage#Canada Percentage]#Response#$[\'data\'][\'citizenship_percentage\']}' ).toInteger()

assert total == response_percentage
mongoClient.close()

